A = (0,105);
B = (0,0);
C = (100,0);
D = (42,0);

draw(A--B--C--cycle);
draw(A--D);

label("$A$", A, NW);
label("$B$", B, SW);
label("$C$", C, SE);
label("$D$", D, S);
